scripts.draw:
function draw(){

    var keys = chartwerk.datamap.sort;

    var table = d3.select("#chart")
        .append("div")
        .attr("id","table")
        .append("table");

    var thead = table.append("thead");

    keys.forEach(function(k){
        thead.append("th")
            .attr("class", "sort")
            .attr("data-sort", k)
            .text(k);
    });

    var tbody = table.append("tbody")
            .attr("class", "list");

    chartwerk.data.forEach(function(d){
        var tr = tbody.append("tr");
        keys.forEach(function(k){
            tr.append("td")
                .attr("class", "td "+ k)
                .text(d[k]);
        });
    });

    var tableOptions = {
      valueNames: keys
    };

    var tableList = new List('table', tableOptions);

    // List

    var list = d3.select("#chart")
        .append("div")
        .attr("id","list")
        .attr("class","hidden");

    list.append("ul")
        .attr("class", "list");

    paras = keys.map(function(k){
        return '<p><span class="list-hed">'+k+'</span> <span class="'+k+'"></span></p>';
    }).join('');

    var listOptions = {
      valueNames: keys,
      item: '<li>'+paras+'</li>'
    };

    var listList = new List('list', listOptions, chartwerk.data);

    function switchStyle(){
        if($("#chart").width() < $("#table")[0].scrollWidth){
            $("#table").addClass("hidden");
            $("#list").removeClass("hidden");
        }
    }
    setTimeout(function(){
        switchStyle();
    }, 100);


}

scripts.styles:
#chartwerk {
    background: inherit;
    font-family: 'din-2014', 'Helvetica Neue', Helvetica, Arial, sans-serif;
    font-weight: 300;
}
#chartwerk table{
    margin:10px 0 20px;
    overflow-x:visible;
    text-align:left;
    width:100%;
}
#chartwerk table th, #chartwerk table td{
    padding:6px 10px 6px 5px;

}
#chartwerk table th{
    border-bottom:2px solid grey;
    cursor:pointer;
    vertical-align: bottom;
    position: relative;
    font-weight: normal;
}
#chartwerk table th:hover{
    background-color:#eee;
}
#chartwerk table th.asc, #chartwerk table th.desc {
    background-color: #eee;
}
#chartwerk table th.asc:after, #chartwerk table th.desc:after, #chartwerk table th:hover:after {
    position: absolute;
    right: 0px;
    top: 7px;
    font-size: 11px;
    color: #999;
    font-weight: normal;
}
#chartwerk table th.asc:after, #chartwerk table th:hover:after {
    content: "\25b2";
}
#chartwerk table th.desc:after {
    content: "\25bc";
}
#chartwerk table tbody tr:first-child td{
    padding-top:10px;
}
#chartwerk table tbody tr{
    border-bottom:1px solid #ccc;
}
#chartwerk table tr:hover{
    background-color:#eee !important;
}
#chartwerk #list{
    margin:0px 0 20px;
    max-height:300px;
    overflow-y:scroll;
}
#chartwerk #list li{
    border-bottom:1px solid #ccc;
    padding: 5px 0;
}

#chartwerk #list p{
    text-align:left;
    margin:1px 0;
    line-height:12px;
    padding:5px 2px;
    font-size:16px;
}
#chartwerk #list p span.list-hed{
    font-size:14px;
    color:grey;
    display:inline-block;
    width:40%;
    word-wrap: normal;
   line-height: 1em;
}

#chartwerk #list p:first-child .list-hed{
    width:100%;
}

#chartwerk #list p{
    padding: 0;
}

#chartwerk #list .list-hed{
    margin-bottom: 4px;
}

#chartwerk #list p:first-child{
    margin-bottom: 16px;
}

#chartwerk #list p:first-child  span:not(:first-child){

    font-weight: 600;
    font-size: 14px;
    line-height: 1.2em;
}

::-webkit-scrollbar{
  width: 6px;
}

::-webkit-scrollbar-track{
  background: rgba(0, 0, 0, 0.1);
  border-radius:5px;
}

::-webkit-scrollbar-thumb{
  background: rgba(0, 0, 0, 0.4);
  border-radius:5px;
}
.hidden{
    display:none !important;
}


