/*shared source byline styles*/
/****************/

.double #chart-footer #author{
    float: right;
}

.single #chart-footer #source{
    font-weight: 400;
    font-size: 10px;
}

.single #chart-footer #author{
    font-weight: 400;
    font-size: 10px;
    float: left;
}

#chartwerk #source,
#chartwerk #author {
    font-size:12px;
    font-weight: 400
    color:#666;
    display: inline-block;
}
#chartwerk #footnote{
    font-size: 12px;
    color:#666;
}

#chartwerk.single #footnote,
#chartwerk.single #source,
#chartwerk.single #author {
    display: block;
}

#chartwerk #chart-footer {
    margin-top: 12px;
}
