scripts.draw:
function draw(){

    var initialProps = {
        dims: {
          single: { width: 260, height: 225 },
          double: { width: 540, height: 250}
        },
    };

    // Returns object with properties and methods representing
    // dimensions, scales, axes, etc.
    var werk = werkHelper.build(initialProps);

    var comma = d3.format(",");

    var div = d3.select("#chart")
        .append("div")
        .style("margin", 
            werk.dims.margins.top + "px " +
            werk.dims.margins.right + "px " +
            werk.dims.margins.bottom + "px " +
            werk.dims.margins.left + "px "
        );


    var response = div.selectAll(".response")
        .data(werk.data)
      .enter().append("div")
        .attr("class","response");

    response.append("p")
    		.text(function(d){ return d.name; });

    response.append("div")
        .attr("class", "bar")
        .style("background-color", chartwerk.axes.color.range[0])
        .style("width", function(d){ return werk.scales.x(d.value) + 'px'; })
      .append("div")
        .attr("class","label")
      .append("p")
        .attr("class", function(d){
            return werk.scales.x(d.value) < 75 ?
                'offset' : '';
        })
        .style("width", function(d){
            return werk.scales.x(d.value) < 75 ?
                werk.dims.div.width - this.parentElement.parentElement.clientWidth + 'px' : '';
        })
        .style("margin-left", function(d){
            return werk.scales.x(d.value) < 75 ?
                this.parentElement.parentElement.clientWidth + 'px' : '';
        })
        .text(function(d){
            return chartwerk.axes.value.prefix + comma(String(d.value)) + chartwerk.axes.value.suffix;
        });


}

scripts.helper:
var werkHelper = {
    parse: function(werk){

        werk.data = chartwerk.data.map(function(d){
            return {
                name: d[chartwerk.datamap.base],
                value: d[chartwerk.datamap.value]
            };
        })
        .sort(function(a, b){ return d3.descending(a.value, b.value); });
    },

    dims: function(werk){
        var s = chartwerk.ui.size;
            w = werk.dims[s].width,
            h = werk.dims[s].height,
            margins = {
                right: chartwerk.margins[s].right * w,
                left: chartwerk.margins[s].left * w,
                top: chartwerk.margins[s].top * h,
                bottom: chartwerk.margins[s].bottom * h
            },
            div = {
                width: w - margins.left - margins.right,
                height: h - margins.top - margins.bottom
            };

        werk.dims.margins = margins;
        werk.dims.div = div;
    },

    scales: function(werk){
        var div = werk.dims.div;
        werk.scales = {
            x: d3.scaleLinear()
                .range([0, div.width]),
        };
    },


    valueDomain: function(werk){
        var max = d3.max(werk.data, function(d) { 
                return d.value; 
            });

        if (chartwerk.axes.value.min && chartwerk.axes.value.max) {
            werk.scales.x.domain(
                [chartwerk.axes.value.min, chartwerk.axes.value.max]
            );
        } else if (chartwerk.axes.value.min) {
            werk.scales.x.domain(
                [chartwerk.axes.value.min, max ]
            );
        } else if (chartwerk.axes.value.max) {
            werk.scales.x.domain(
                [0, chartwerk.axes.value.max]
            );
        } else {
            werk.scales.x.domain([0, max]);
        }

        werk.scales.x.nice();
    },


    // Build dims, scales and axes.
    build: function(werk){
        this.parse(werk);
        this.dims(werk);
        this.scales(werk);
        this.valueDomain(werk);
        return werk;
    },
};

scripts.styles:
#chartwerk {
    width:560px;
    font-family: 'proxima-nova', arial, sans-serif;
    background: transparent;
}
#chartwerk div{
    font-weight: 200;
}
#chartwerk.single{
    width:270px;
    float:left;
    overflow:hidden;
    margin:10px 30px 10px 0;
}
#chartwerk #headline { 
    font-weight:bold;
    font-size:24px;
}
#chartwerk #chart-header{
    line-height: 1.45em;
    font-weight: 400;
}

#chartwerk #chart-header #headline{
    line-height: 1.1em;
}

#chartwerk.single #chart-header #headline{
    line-height: 1em;
}

#chartwerk #headline { 
    font-weight:bold;
    font-size:25px;
    line-height: 1.05em;
    margin: 0;
}
#chartwerk #chatter {
    margin: 6px 0px 12px;
    font-size: 17px;
}

#chartwerk.single #chatter {
    margin: 8px 0px 10px;
    font-size: 14px;
    line-height: 1.2em;
}
#chartwerk.double #chart-footer #author{
    float: right;
}

#chartwerk.single #chart-footer #source{
    font-weight: 400;
    font-size: 10px;
}

#chartwerk.single #chart-footer #author{
    font-weight: 400;
    font-size: 10px;
    float: left;
}


#chartwerk #footnote,
#chartwerk #source,
#chartwerk #author {
    font-size:12px;
    font-weight: 400
    color:#666;
    display: inline-block;
}

#chartwerk.single #footnote,
#chartwerk.single #source,
#chartwerk.single #author {
    display: block;
}

#chartwerk #chart-footer {
    margin-top: 12px;
}

#chartwerk #author{
    float: right;
}

#chartwerk #source{
    vertical-align: text-top;
}
#chartwerk #chart{
    background: transparent;
}
#chartwerk .response{
    margin-top:10px;
}
#chartwerk .response p{
    font: 13px/16px 'proxima-nova', arial, sans-serif;
    font-weight: 200;
}
#chartwerk #chart .bar{
    height:24px;
    display:inline-block;
    margin-top:2px;
}
#chartwerk #chart .bar .label{
    width:100%;
    text-align:right;
    font-family:'franklin-gothic-urw', arial, sans-serif;
}
#chartwerk #chart .bar .label p{
    padding-right:5px;
    padding-top:4px;
    font-family:'franklin-gothic-urw', arial, sans-serif;
    font-size:14px;
    line-height:18px;
    color:#fff;
}
#chartwerk #chart .bar .label p.offset{
    text-align:left;
    padding-left:3px;
    color: #666;
}
