scripts.draw:
function draw(){

    var initialProps = {
        dims: {
          single: { width: 260, height: 225 },
          double: { width: 540, height: 250}
        },
    };

    // Returns object with properties and methods representing
    // dimensions, scales, axes, etc.
    var werk = werkHelper.build(initialProps);


    var svg = d3.select("#chart")
        .append("svg")
        .style("background-color","transparent")
        .attr("width", werk.dims.svg.width + werk.dims.margins.left + werk.dims.margins.right)
        .attr("height", werk.dims.svg.height + werk.dims.margins.top + werk.dims.margins.bottom)
      .append("g")
        .attr("transform", "translate(" + werk.dims.margins.left + "," + werk.dims.margins.top + ")");

    chartwerk.axes.base.shadedRegions.forEach(function(shade) {
        var rectLeft = werk.scales.x(werk.parsers.base(shade.min));
        var rectRight = werk.scales.x(werk.parsers.base(shade.max));
        var minWidth = 2;
        var width = Math.max(2, rectRight - rectLeft);
        if (rectRight === rectLeft) {
            svg.append('line')
                .attr("class",'solid-line')
                .attr("y1", 0)
                .attr("y2", werk.scales.y.range()[0])
                .attr("x1", rectLeft)
                .attr("x2", rectLeft);
        } else {
            svg.append('rect')
                .attr("class","shaded-area")
                .attr('y', 0)
                .attr('x', rectLeft)
                .attr('width', width)
                .attr('height', werk.scales.y.range()[0]); 
        }
    });

    chartwerk.axes.value.shadedRegions.forEach(function(shade) {
        var rectMin = werk.scales.y(werk.parsers.value(shade.min));
        var rectMax = werk.scales.y(werk.parsers.value(shade.max));
        var height = Math.max(2, rectMin - rectMax);
        if (rectMin === rectMax) {
            svg.append('line')
                .attr("class",'dotted-line')
                .attr("x1", 0)
                .attr("x2", werk.scales.x.range()[1])
                .attr("y1", rectMin)
                .attr("y2", rectMin);
        } else {
            svg.append('rect')
                .attr("class","shaded-area")
                .attr('x', 0)
                .attr('y', rectMax)
                .attr('width', werk.scales.x.range()[1])
                .attr('height', height); 
        }
    });

    svg.append("g")
        .attr("class", "y axis")
        .call(werk.axes.y)
      .append("text")
        .attr("class","label")
        .attr("y", -werk.dims.margins.top + 15)
        .attr("x", -werk.dims.margins.left)
        .style("text-anchor", "start")
        .text(chartwerk.axes.value.label);

    svg.append("g")
        .attr("class", "x axis")
        .attr("transform", "translate(0," + werk.dims.svg.height + ")")
        .call(werk.axes.x);

    var dots = svg.append("g")
        .attr("class", "dots");


    dots.selectAll("circle.dot")
        .data(werk.data)
      .enter().append("circle")
        .attr("class",function(d, i){ return "dot n-" + i.toString()})
        .attr("r", 6)
        .attr("cx", function(d) { return werk.scales.x(d.x); })
        .attr("cy", function(d) { return werk.scales.y(d.y); })
        .style("fill", werk.scales.color());

    var voronois = svg.selectAll("path.voronoi")
        .data(werk.scales.voronoi.polygons(werk.data))
      .enter().append("path")
        .attr("class", "voronoi")
        .attr("d", function(d, i) { return "M" + d.join("L") + "Z"; })
        .style("fill", "none")
        .style("pointer-events", "all")
        .on("mouseout",  hideTooltip)
        .on("mouseover", showTooltip);



    var tooltip = d3.select("#chart")
      .append("div")
        .attr("class","tooltip")
        .style("position","absolute");
    tooltip
      .append("div")
      .attr("class","value");



    function hideTooltip(d, i){
        d3.select(".tooltip")
          .style("opacity", 0);
        svg.selectAll('circle.n-' + i.toString())
            .classed("active", false)
            .moveToBack();
    }

    function showTooltip(d, i){
        console.log(i);
        if (!d.data.tooltip) return;
        var x = werk.scales.x(d.data.x);
        var y = werk.scales.y(d.data.y);
        console.log(x, y);
        tooltip
            .style('top', function() {
                return y < (werk.dims.svg.height / 2) ?
                    (y + 20) + 'px' :
                    (y - 20) + 'px';
            })
            .style('left', function() {
                return x < (werk.dims.svg.width / 2) ?
                    (x + 40) + 'px' :
                    (x - 40) + 'px';
            })
            .style('opacity', 1);
        tooltip.select('div')
            .text(d.data.tooltip);

        svg.selectAll('circle.n-' + i.toString())
            .classed("active", true)
            .moveToFront();
    }
}

scripts.helper:
var werkHelper = {
    parse: function(werk){
        werk.parsers = {
          base: function(d){ return +d; },
          value: function(d){ return +d; }
        };

        werk.data = chartwerk.data.map(function(d){
            return {
                x: werk.parsers.base(d[chartwerk.datamap.base]),
                y: werk.parsers.value(d[chartwerk.datamap.value]),
                tooltip: chartwerk.datamap.custom.tooltip === "" ? null :
                    d[chartwerk.datamap.custom.tooltip],
            };
        });

        // Get max and min of data 
        var xMax = d3.max(werk.data, function(d){return d.x; }),
	        xMin = d3.min(werk.data, function(d){return d.x; }),
	        yMax = d3.max(werk.data, function(d){return d.y; }),
	        yMin = d3.min(werk.data, function(d){return d.y; });

        werk.dataDims = {
            xMin: xMin,
            xMax: xMax,
            yMin: yMin,
            yMax: yMax
        };
    },

    dims: function(werk){
        var s = chartwerk.ui.size;
            w = werk.dims[s].width,
            h = werk.dims[s].height,
            margins = {
                right: chartwerk.margins[s].right * w,
                left: chartwerk.margins[s].left * w,
                top: chartwerk.margins[s].top * h,
                bottom: chartwerk.margins[s].bottom * h,
            },
            svg = {
                width: w - margins.left - margins.right,
                height: h - margins.top - margins.bottom,
            };

        werk.dims.margins = margins;
        werk.dims.svg = svg;
    },

    scales: function(werk){
        var svg = werk.dims.svg;
        werk.scales = {
            x: d3.scaleLinear()
                .range([0, svg.width]),
            y: d3.scaleLinear()
                .range([svg.height, 0]),
            color: function(d){return chartwerk.axes.color.range[0];},
        };
        werk.scales.voronoi = d3.voronoi()
            .x(function(d){ return werk.scales.x(d.x); })
            .y(function(d){ return werk.scales.y(d.y); })
            .size([svg.width, svg.height]);
    },

    baseDomain:  function(werk){
        var domain = [werk.dataDims.xMin, werk.dataDims.xMax];
        var base = chartwerk.axes.base;

        switch (isNaN(base.min) || base.min) {
            case true:
            case null:
            case '':
                break;
            default:
                domain.splice(0, 1, base.min);
        }
        switch (isNaN(base.max) || base.max) {
            case null:
            case true:
            case '':
                break;
            default:
                domain.splice(1, 1, base.max);
        }

        werk.scales.x
            .domain(domain)
            .nice();
    },

    valueDomain: function(werk){
        var domain = [werk.dataDims.yMin, werk.dataDims.yMax];
        var value = chartwerk.axes.value;

        switch (isNaN(value.min) || value.min) {
            case true:
            case null:
            case '':
                break;
            default:
                domain.splice(0, 1, value.min);
        }
        switch (isNaN(value.max) || value.max) {
            case null:
            case true:
            case '':
                break;
            default:
                domain.splice(1, 1, value.max);
        }

        werk.scales.y
            .domain(domain)
            .nice();
    },

    axes: function(werk){
        werk.axes = {
          x: d3.axisBottom(werk.scales.x)
              .tickSizeInner(-werk.dims.svg.height)
              .tickSizeOuter(0)
              .tickPadding(7)
              .tickFormat( function(d){ 
                var formatter = werk.scales.x.tickFormat();
                if (d >= 0) {
                  return formatter(d) === '0' ? '0' : 
                  chartwerk.axes.base.prefix + formatter(d) + chartwerk.axes.base.suffix;
                } else {
                  return "-" + chartwerk.axes.base.prefix + formatter(Math.abs(d)) + chartwerk.axes.base.suffix;
                }
              }),
          y: d3.axisLeft(werk.scales.y)
              .tickSizeInner(-werk.dims.svg.width)
              .tickSizeOuter(0)
              .tickPadding(7)
              .tickFormat( function(d){ 
                var formatter = werk.scales.y.tickFormat();
                if (d >= 0) {
                  return formatter(d) === '0' ? '0' : 
                  chartwerk.axes.value.prefix + formatter(d) + chartwerk.axes.value.suffix;
                } else {
                  return "-" + chartwerk.axes.value.prefix + formatter(Math.abs(d)) + chartwerk.axes.value.suffix;
                }
              }),
        };
    },

    baseTicks: function(werk){
        var s = chartwerk.ui.size;
        if (chartwerk.axes.base.format[s].customTicks.length > 0) { 
            werk.axes.x.tickValues(
                chartwerk.axes.base.format[s].customTicks
            );
        } else {
            werk.axes.x.ticks(
              chartwerk.axes.base.format[s].ticks
            );
        }
    },

    valueTicks: function(werk){
        var s = chartwerk.ui.size;
        if (chartwerk.axes.value.format[s].customTicks.length > 0) { 
            werk.axes.y.tickValues(
                chartwerk.axes.value.format[s].customTicks
            );
        } else {
            werk.axes.y.ticks(
              chartwerk.axes.value.format[s].ticks
            );
        }
    },

    // Build dims, scales and axes.
    build: function(werk){
        this.parse(werk);
        this.dims(werk);
        this.scales(werk);
        this.baseDomain(werk);
        this.valueDomain(werk);
        this.axes(werk);
        this.baseTicks(werk);
        this.valueTicks(werk);
        return werk;
    },
};

